# Function to deduplicate the list
def deduplicate_games(games):
    seen = set()
    unique_games = []
    for game in games:
        if (game[0], game[1]) not in seen:  # Assuming title and release_year uniquely identify a game
            unique_games.append(game)
            seen.add((game[0], game[1]))
    return unique_games

# Deduplicate the list of games
deduplicated_games = deduplicate_games(games)

# Print the deduplicated list with commas at the end of each entry
print("Deduplicated games list:")
for game in deduplicated_games:
    print(f"{game},")